con:config.script:
import com.eviware.soapui.support.XmlHolder

Addrun=testRunner.testCase.testSteps["Addition"]
Addrun.run(testRunner,context)
//Method - expand is used to get response or request in text format
GetResponse = context.expand('${Addition#response}')
//text format is converted to xml format and placed in xmlholder
ConvertXml = new XmlHolder(GetResponse)
//From XML get node value of Addresult and place in GetValue
GetValue = ConvertXml.getNodeValue("//*:AddResult")
log.info GetValue
//set property of getvalue to intB
context.setProperty("IntB",GetValue)
//run Multiple with intA already set from above value
Multirun=testRunner.testCase.testSteps["Multiply"]
Multirun.run(testRunner,context)
